import React, { Component, useState } from 'react';
import AddPost from './newPostPage';
import component from './component.css';
import { Button } from 'react-bootstrap';

export class OpenNewPostModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            show: false
        };
    }

    showModal = () => {
        this.setState( { show: true });
        console.log(this.state.show);
    }

    hideModal = () => {
        this.setState( { show: false });
    }

    render() {
        return (     
            <div>
                <Modal show={this.state.show} handleClose={this.hideModal} user={this.props.user}>
                </Modal>
                <Button variant="outline-warning" onClick={this.showModal}>New post</Button>
            </div>
        )
    }
}

const Modal = ({ handleClose, show, user }) => {
    const showHideClassName = show ? "modal display-block" : "modal display-none";
    console.log(showHideClassName);
    return(
        <div className={showHideClassName}>
            <section>
                <AddPost displayName={user}></AddPost>
                <Button onClick={handleClose} className="close">X</Button>
            </section>
        </div>
        
    );
};
