import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component, useState } from 'react';
import { Collapse, Container, Row, Col } from 'react-bootstrap';
import CreateUser from './components/createuser';
import Header from './components/Header';
import { getAllPosts } from './services/getPosts';
import { DisplayBoard } from './components/displayBoard';
import { createPost } from './services/newPost';
import { CreatePost } from './components/createPost';
import { OpenLoginModal } from './components/openLoginModal';
import { OpenNewPostModal } from './components/openNewPostModal';
import NavBarCustom from './components/navBar';
import RenderPosts from './components/renderPosts';

class App extends Component {
  state = {
    user: {},
    posts: ["Hello"],
    post: {data: "Game"}
  }

  CreateUser = (e) => {
    CreateUser(this.state.user)
    .then(response => {
      console.log(response);

    });
  }

  CreatePost = (e) => {
    createPost(this.state.post)
    .then(response => {
      console.log(response);
    })
  }

  getallPosts = () => {
    getAllPosts()
    .then(posts => {
      console.log(posts);
      this.setState({posts: posts})
    });
  }

//<CreatePost createPost={this.CreatePost}></CreatePost>
  render() {
    return(
      <div className="App-header">
        <NavBarCustom></NavBarCustom>
        <DisplayBoard getAllPosts={this.getallPosts}></DisplayBoard>
        <Container>
          <Row>
              <RenderPosts></RenderPosts>
          </Row>
        </Container>
      </div>
    );
  }
}


export default App;
