import React, { Component } from 'react';
import { getAllPosts } from './../services/getPosts';

class RenderPosts extends Component {
    constructor(props) {
        super(props);
        this.state = {
            posts: []
        };
    }

    componentDidMount(){
        this.getallPosts();
        console.log("ComponentDidMount called.");
    }

    getallPosts = () => {
        getAllPosts()
        .then(posts => {
          console.log(posts);
          this.setState({posts: posts})
        });
      }

      render(){
          return(
              <div className="list-group">
                  {
                      this.state.posts.map(function(post, index) {
                          return (
                          <a href="#" key={index} className="list-group-item active">
                            <h4 className="list-group-item-heading">{post.title}</h4>
                            <h3 className="list-group-item-heading">{post.user}</h3>
                            <h3 className="list-group-item-heading">{post.time}</h3>
                            <p className="list-group-item-text">{post.subject}</p>
                          </a>
                          )
                      })
                  }
              </div>
          )
      }
}

export default RenderPosts;
