import { createPost } from './../services/newPost';
import { Form, Button } from 'react-bootstrap';
const { Component } = require("react");

class AddPost extends Component {

    constructor(){
        super();
        this.state={
            title: "",
            subject: "",
            error: "",
            displayName: "",
        };

        this.handleTitleChange = this.handleTitleChange.bind(this);
        this.handleSubjectChange = this.handleSubjectChange.bind(this);
        this.dismissError = this.dismissError.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    dismissError(){
        this.setState({ error: "" });
    }

    handleSubmit(evt){
        evt.preventDefault();

        if(!this.state.title){
            return this.setState({ error: "Title is required" });
        }
        if(!this.state.subject) {
            return this.setState({ error: "Subject is required" });
        }


        this.CreatePost();
        return this.setState({ error: "" });
    }

    handleTitleChange(evt) {
        this.setState({
            title: evt.target.value,
        });
    }

    handleSubjectChange(evt) {
        this.setState({
            subject: evt.target.value,
        });
    }

    CreatePost = (e) => {
        const {title, subject, error, displayName} = this.props;
        createPost({title, subject, error, displayName})
        .then(response => {
          console.log(response);
        })
      }

    render() {
        return(
        <div className="col-md-5">
            <div className="form-are">
                <Form onSubmit={this.handleSubmit}>
                    {
                        this.state.error &&
                        <h3 onClick={this.dismissError}>
                            <Button onClick={this.dismissError}>X</Button>
                            {this.state.error}
                        </h3>
                    }
                    <Form.Label>Logged in as: {this.props.displayName}</Form.Label>
                    <div className="form-group">
                        <Form.Label>Title</Form.Label>
                        <Form.Control className="username" type="text" placeholder="Enter title" value={this.state.title} onChange={this.handleTitleChange}></Form.Control>
                    </div>
                    <div className="form-group">
                        <Form.Label>Subject</Form.Label>
                        <Form.Control as="textarea" rows={3} className="username" type="text" placeholder="Enter subject" value={this.state.subject} onChange={this.handleSubjectChange}></Form.Control>
                    </div>
                    <Form.Control className="submit" type="submit" value="Submit"></Form.Control>
                </Form>
            </div>
        </div>
    )}
}

export default AddPost;
