import { isAuthenticated } from './services/isAuthenticated';

console.log(localStorage.token);

class App extends Component {
  state = {
    user: 'Not currently signed in'
  }

  componentDidMount(){
    console.log(localStorage.token);
    const token = localStorage.token;
    if(token === undefined){
      this.setState({
        user: "Not currently signed in"
      })

    } else {
      this.IsAuthenticated(localStorage.token);
    }

  }

  IsAuthenticated = (e) => {
    isAuthenticated(e)
    .then(response => {
      console.log(response);
        this.setState({
          user: response.user
        });
    })
  }


//<CreatePost createPost={this.CreatePost}></CreatePost>
  render() {
    return(
      <div className="App-header">
        <NavBarCustom user={this.state.user}></NavBarCustom>
        <DisplayBoard getAllPosts={this.getallPosts}></DisplayBoard>
        <Container>
          <Row>
              <RenderPosts></RenderPosts>
          </Row>
        </Container>
      </div>
    );
  }
}


export default App;
