import { createPost } from './../services/newPost';
import { Form, Button } from 'react-bootstrap';
const { Component } = require("react");

class AddPost extends Component {

    constructor(){
        super();
        this.state={
            title: "",
            subject: "",
            error: "",
            displayName: "",
        };

        this.handleTitle = this.handleTitleChange.bind(this);
        this.handleSubject = this.handleSubjectChange.bind(this);
        this.dismissError = this.dismissError.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    dismissError(){
        this.setState({ error: "" });
    }

    handleSubmit(evt){
        evt.preventDefault();

        if(!this.state.title){
            return this.setState({ error: "Title is required" });
        }
        if(!this.state.subject) {
            return this.setState({ error: "Subject is required" });
        }


        this.CreatePost();
        return this.setState({ error: "" });
    }

    handleTitleChange(evt) {
        this.setState({
            title: evt.target.value,
        });
    }

    handleSubjectChange(evt) {
        this.setState({
            subject: evt.target.value,
        });
    }

    CreatePost = (e) => {
        const {title, subject, error, displayName} = this.props;
        createPost({title, subject, error, displayName})
        .then(response => {
          console.log(response);
        })
      }
      //<Form.Label>Logged in as: {this.props.displayName}</Form.Label><Form.Label>Title</Form.Label> <Form.Label>Subject</Form.Label>
    render() {
        return(
        <div className="col-md-5">
            
        </div>
    )}
}

export default AddPost;
